    def get_components_by_type_and_price(
        component_type,
        min_price=0,
        max_price=999999
        ) -> dict:
        """
        Получение компонентов определенного типа с фильтрацией по цене
        {
        'component_name': 'Видеокарта KFA2 GeForce GTX 1060 EX, 6GB GDDR5, PCI-E, 6144MB, 192 Bit',
                'component_type': "videokarty",
                'url': "www.market.yandex/fgfdhgfd/fhhfhfd",
                'image_url': ["https://avatars.mds.yandex.net/get-mpic/4409630/2a00000193e57d8c83efd97c8d7b1a0125a9/501x668", ""],
                'price': 1500.00,
                'articul_yandex': '5537064366',
                'fullSpecsGrouped': {
                    'groups': [
                        {
                            'name': 'Графический процессор',
                            'items': [
                                {'name': 'Бренд', 'value': 'KFA2'}
                            ]
                        }
                    ]
                }
        }

        """
        try:
            min_price = float(min_price)
            max_price = float(max_price)
            if max_price:
                links = Links.query.filter(
                    and_(
                        Links.component == component_type,
                        Links.price >= min_price,
                        Links.price <= max_price,
                        Links.is_actual == True
                    )
                ).all()
            else:
                links = Links.query.filter(
                    and_(
                        Links.component == component_type,
                        Links.price >= min_price,
                        Links.is_actual == True
                    )
                ).all()
            link_data = []
            for link in links:
                items = {}
                for characteristic in link.characteristics:
                    if characteristic.group not in items:
                        items[characteristic.group] = {}
                        items[characteristic.group]['items'] = []

                    items[characteristic.group]['items'].append(
                        {
                            'name': characteristic.name,
                            'value': characteristic.value
                        }
                        )
                characteristics_grouped = []
                for key, value in items.items():
                    characteristics_grouped.append(
                        {
                            'name': key,
                            'items': value['items']
                            }
                    )

                # Получаем URL изображений (если есть)
                image_urls = []
                try:
                    image_urls = [link.image_url] if link.image_url else []
                except Exception:
                    image_urls = []

                # Добавление заголовка и характеристик в результирующий список
                link_data.append({
                    'component_name': link.title,
                    'component_type': link.component,
                    'url': link.link,
                    'image_url': image_urls,
                    'price': link.price,
                    'articul_yandex': link.articul_yandex,
                    'fullSpecsGrouped': {'groups': characteristics_grouped}
                })
            return link_data
        except Exception as e:
            print("Ошибка при получении запроса с отбором по цене:", e)

